  `FLOW_COM` longtext COMMENT '第一步默认数值设置',
  `FLOW_UPCOM` longtext COMMENT '修改默认数值设置',
  `FLOW_REMARK` varchar(250) DEFAULT NULL COMMENT '备注信息',
  `FLOW_NO` varchar(2) DEFAULT NULL COMMENT '序号',
  `FLOW_COVER` varchar(1) DEFAULT NULL COMMENT '调用后是否覆盖',
  `FLOW_SYNC` varchar(1) DEFAULT NULL COMMENT '0同步1异步',
