  `DATA_CALL_CON` longtext COMMENT '调用条件\r\n            {"arg":"t.a,b,c","functionStr":" if(''02''==''01''){''1''}else{''0''}"}/这里是JAVASCRIPT语句',
  `DATA_CALL_CONFLAG` varchar(1) DEFAULT NULL COMMENT '条件不符合是否异常 0 不异常 1 异常',
  `DATA_SUC_FLAG` varchar(250) DEFAULT NULL COMMENT '调用成功标识',
  `DATA_FAIL_CONTINUE` varchar(1) DEFAULT NULL COMMENT '调用失败后是否继续',
  `DATA_CALL_DES` varchar(50) DEFAULT NULL COMMENT '调用说明',
  `DATA_CALL_CDES` varchar(50) DEFAULT NULL COMMENT '调用条件说明',
  `GMT_CREATE` datetime DEFAULT NULL COMMENT '创建时间',
  `GMT_MODIFIED` datetime DEFAULT NULL COMMENT '修改时间',
  `MEMO` varchar(250) DEFAULT NULL COMMENT '备注',
  `DATA_STATE` int(11) DEFAULT NULL COMMENT '状态',
  `TENANT_CODE` varchar(32) DEFAULT NULL,
  PRIMARY KEY (`FLOW_ID`),
  UNIQUE KEY `UDX_SF_FLOW_CODE` (`FLOW_CODE`),
  KEY `IDX_SF_FLOW_NO` (`FLOW_NO`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='服务编排';

